mapper.select:
 		SELECT COUNT(*)
		FROM APPROVAL_DOC

			WHERE STATUS = #{status}

SELECT A.DOC_NO, D.DEPT_NAME,T.DOC_NAME, A.DOC_TITLE, TO_CHAR(A.CREATE_DATE,'YYYY-MM-DD')"CREATE_DATE", A.STATUS FROM APPROVAL_DOC A JOIN DEPARTMENT D ON (A.DEPT_CODE = D.DEPT_CODE) JOIN DOC_TYPE T ON (A.DOC_TYPE = T.DOC_TYPE)
WHERE STATUS = #{status}
ORDER BY A.CREATE_DATE DESC

SELECT * FROM (SELECT ROWNUM, A.* FROM (SELECT DOC_NO ,D.DEPT_NAME ,T.DOC_NAME ,DOC_TITLE ,TO_CHAR(P.CREATE_DATE,'YYYY-MM-DD')"CREATE_DATE" FROM APPROVAL_DOC P JOIN DEPARTMENT D ON (P.DEPT_CODE = D.DEPT_CODE) JOIN DOC_TYPE T ON (P.DOC_TYPE = T.DOC_TYPE)
WHERE P.STATUS = #{status}
ORDER BY P.CREATE_DATE DESC ) A) WHERE ROWNUM BETWEEN 1 AND 5
